import React from "react";
export const MyComponent = () => {
    const [username, setUsername] = React.useState("");
    // onLoad
    React.useEffect(() => {
        setTimeout(() => {
            setUsername("John")
        }, 1500)
    })  //ONLOAD
    return (
        <div>
            <h4>{username}</h4>
            <input value = {username} 
             onChange = {e => setUsername}
            />
        </div>
    )
}
