        // }, 1500)
        fetch(
            'http://neteasecloudmusicapi.zhaoboy.com/toplist/detail'
        .then(data => data.json())
        .then(data => {
            console.log(data);
        })
        )
    }, [])  //ONLOAD
    return (
        <div>
            <h4>{username}</h4>
            <input value = {username} 
             onChange = {e => setUsername}
            />
        </div>
    )
}
